#!/bin/sh
exec st -c floatterm -g 160x47 -e zsh -c 'select loc in Stillwater Livingston Prague "San Diego" Moon Read Default ; do case $loc in Stillwater) curl "v2.wttr.in/SWO?m" ; break;; Livingston) curl "v2.wttr.in/KLVM?m" ; break;; Prague) curl "v2.wttr.in/Prague?m" ; break;; "San Diego") curl "v2.wttr.in/San_Diego?m" ; break;; Moon) curl "v2.wttr.in/moon" ; break;; Read) read foo ; curl "v2.wttr.in/$foo?m" ; break ;; Default) curl "v2.wttr.in/?m"; break ;; esac ; done ; read foo'
